import React, { Component } from 'react';
import f1 from './img/drugs/f1.png';
import f2 from './img/drugs/f2.png';
import './css/druglist.css'

const f1title = "Asprina";
const f1text = "descrizione asprina";

const f2title = "Tachipirina";
const f2text = "La 1000";

class DrugList extends Component {

    render(){
        return(
          <div id="side" className="row">
            <Item id="1" img={f1} />
            <Item id="2" img={f2} />
          </div>
        );
    }
}

class Item extends Component {

  constructor(props) {
    super(props);

    // This binding is necessary to make `this` work in the callback
    this.select = this.select.bind(this);
  }

  select(props){
    return(
    <img src={this.props.img} className="border border-primary"/>
    );
  }

  render(){
      return(
        <div className="border border-primary">
          <button onClick={this.select}><img src={this.props.img} /></button>
        </div>
      );
  }
}

export default DrugList;
