import React, { Component } from 'react';
import f1 from './img/drugs/f1.png';
import f2 from './img/drugs/f2.png';
import './css/druglist.css'

const f1title = "Asprina";
const f1text = "descrizione asprina";

const f2title = "Tachipirina";
const f2text = "La 1000";

class DrugList extends Component {

    render(){
        return(
<div id="demo" className="carousel slide" data-ride="carousel">

<ul classNameName="carousel-indicators">
  <li data-target="#demo" data-slide-to="0" className="active"></li>
  <li data-target="#demo" data-slide-to="1"></li>
  <li data-target="#demo" data-slide-to="2"></li>
</ul>

<div className="carousel-inner">
  <div className="carousel-item active">
    <img src={f1} alt="Los Angeles" />
  </div>
  <div className="carousel-item">
    <img src={f2} alt="Chicago" />
  </div>
  <div className="carousel-item">
    <img src={f3} alt="New York" />
  </div>
</div>

<a className="carousel-control-prev" href="#demo" data-slide="prev">
  <span className="carousel-control-prev-icon"></span>
</a>
<a className="carousel-control-next" href="#demo" data-slide="next">
  <span className="carousel-control-next-icon"></span>
</a>

</div>
        );
    }
}

export default DrugList;
