import React, { Component } from 'react';
import f1 from './img/drugs/f1.png';
import f2 from './img/drugs/f2.png';
import './css/druglist.css'

const f1title = "Asprina";
const f1text = "descrizione asprina";

const f2title = "Tachipirina";
const f2text = "La 1000";

class DrugList extends Component {

    render(){
        return(
        <div id="slider" className="carousel slide m-20" data-ride="carousel">
            <div className="carousel-inner">

              <div className="carousel-item active">
                <img className="d-block w-100" src={f1} alt="Prima slide"/>
                <div className="carousel-caption d-none d-md-block">
                  <h5>{f1title}</h5>
                  <p>{f1text}</p>
                </div>
              </div>

              <div className="carousel-item">
                <img className="d-block w-100" src={f2} alt="Seconda slide"/>
                <div className="carousel-caption d-none d-md-block">
                  <h5>{f2title}</h5>
                  <p>{f2text}</p>
                </div>
              </div>

            </div>

            <a className="carousel-control-prev" href="#slider" role="button" data-slide="prev">
              <span className="carousel-control-prev-icon" aria-hidden="true"></span>
              <span className="sr-only">Precedente</span>
            </a>
            <a className="carousel-control-next" href="#slider" role="button" data-slide="next">
              <span className="carousel-control-next-icon" aria-hidden="true"></span>
              <span className="sr-only">Successivo</span>
            </a>
          </div>
        );
    }
}

export default DrugList;
