import React, { Component } from 'react';
import f1 from './img/drugs/f1.png';
import f2 from './img/drugs/f2.png';
import './css/druglist.css'

const f1title = "Asprina";
const f1text = "descrizione asprina";

const f2title = "Tachipirina";
const f2text = "La 1000";

class DrugList extends Component {

    render(){
        return(
          <div id="container">
            <div id="slider" class="carousel slide p-20" data-ride="carousel">
              <div class="carousel-inner">

                <div class="carousel-item active">
                  <img class="d-block w-100" src={f1} alt="Prima slide"/>
                  <div class="carousel-caption d-none d-md-block">
                    <h5>{f1title}</h5>
                    <p>{f1text}</p>
                  </div>
                </div>

                <div class="carousel-item">
                  <img class="d-block w-100" src={f2} alt="Seconda slide"/>
                  <div class="carousel-caption d-none d-md-block">
                    <h5>{f2title}</h5>
                    <p>{f2text}</p>
                  </div>
                </div>

              </div>

              <a class="carousel-control-prev" href="#slider" role="button" data-slide="prev">
                <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                <span class="sr-only">Precedente</span>
              </a>
              <a class="carousel-control-next" href="#slider" role="button" data-slide="next">
                <span class="carousel-control-next-icon" aria-hidden="true"></span>
                <span class="sr-only">Successivo</span>
              </a>
            </div>
          </div>
        )
    }
}

export default DrugList;
